import { Component, OnDestroy, OnInit } from '@angular/core';
import { TaskService } from 'src/app/task.service';
import { Task } from 'src/app/task';
import { FormControlName, FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Subscription } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-add-task',
  templateUrl: './add-task.component.html',
  styleUrls: ['./add-task.component.css']
})
export class AddTaskComponent implements OnInit, OnDestroy {

  task_form!: FormGroup;
  task!: Task;
  private sub!: Subscription;

  constructor(private fb: FormBuilder, private route: ActivatedRoute, private router: Router, task_service: TaskService) {}

  ngOnInit(): void {
      this.task_form = this.fb.group({
        id: '',
        name: '',
        parent_task: '',
        child_task: [],
        create_by: '',
        create_date: '',
        status: '',
      });

      this.sub = this.route.paramMap.subscribe(param => {
        const id = param.get('id');
        if(id == '0') {
          const task: Task ={id: '', name: '', parent_task: '', child_task: [], create_by: '', create_date: '',
          status: '',}
        }
      })
  }

  ngOnDestroy(): void {

  }
}
