import { Component } from '@angular/core';
import { TaskService } from 'src/app/task.service';
import { Task } from 'src/app/task';
import { TaskListComponent } from '../task-list/task-list.component';

@Component({
  selector: 'app-add-task',
  templateUrl: './add-task.component.html',
  styleUrls: ['./add-task.component.css']
})
export class AddTaskComponent {

  task_name: string = "" ;
  new_task!: Task;
  mess: string = "";

  constructor(private task_service: TaskService) {
    // this.new_task.name = "";
    // this.new_task.id = "";
    // this.new_task.create_date = "";
    // this.new_task.create_by = "";
    // this.new_task.parent_task = "";
    // this.new_task.parent_task = "";
    // this.new_task.child_task = [];
  }


  addTask() {
    this.new_task.name = this.task_name;

    this.task_service.createTask(this.new_task).subscribe({
      error: (err) => {
        this.mess = "done" 
      }
    })
  }


}
<p>add-task works!  {{mess}} </p>
<input type="text" [(ngModel)]="task_name">
<div>
    <button (click)="addTask()" class="bg-blue-200 rounded-full">Thêm</button>
</div>
