import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  title = 'app';
}
<body class="bg-indigo-50 h-screen">
  <div class="flex flex-col h-full w-full">
    <app-header></app-header>
    <main class="container h-full w-full flex m-0 p-0">
        <div class="bg-white rounded-r-lg">
            <app-sidebar></app-sidebar>
        </div>
        <div class="ml-3 w-full bg-white rounded-l-lg"> 
          <app-boardcontent></app-boardcontent>
        </div>
        <div></div>
    </main>
  </div>
</body>
