import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import {CdkDragDrop, CdkDropList, CdkDrag, moveItemInArray} from '@angular/cdk/drag-drop';
import {NgFor} from '@angular/common';
import { Task } from 'src/app/task';
import { TaskService } from 'src/app/task.service';

@Component({
  selector: 'app-expandgroup',
  templateUrl: './expandgroup.component.html',
  styleUrls: ['./expandgroup.component.css'],
  standalone: true,
  imports: [CdkDropList, NgFor, CdkDrag],
})
export class ExpandgroupComponent implements OnInit {
  @Input() collapsee: boolean = false;
  @Output() collapseeChange = new EventEmitter<boolean>();
  task_list: Task[] = [];

  changetocollapse() {
    this.collapseeChange.emit(true);
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.task_list, event.previousIndex, event.currentIndex);
  }

  // ngOnInit(): void {
  //   this.getTaskList();
  // }

  ngOnInit(): void {
    this.task_form = this.fb.group({
      id: '',
      name: '',
      parent_task: '',
      child_task: [],
      create_by: '',
      create_date: '',
      status: '',
    });

    this.sub = this.route.paramMap.subscribe(param => {
      const id = param.get('id');
      if(id == '0') {
        const task: Task = {id: "0", name: "", parent_task: "", child_task: [], create_by: "", create_date: "", status: "",};
        this.displayTask(task);
      }
      else {
        this.getTask(id);
      }
    });
}

  // constructor(private task_service: TaskService) {}
  constructor(private fb: FormBuilder, private route: ActivatedRoute, private task_service: TaskService) {}

  getTaskList(): void {
    this.task_service.getTaskList().subscribe( data =>{
      this.task_list = data;
    })
  }

  deleteTask(delete_task: Task): void {
    if(confirm(`Are you sure to delete this task ${delete_task.name}?`)) {
      this.task_service.deleteTask(delete_task.id).subscribe({
        error: () =>this.reloadList()
      })
    }
  }

  reloadList(): void {
    this.getTaskList();
  }

  

  task_form!: FormGroup;
  task!: Task;
  private sub!: Subscription;
  mess: string = "";





  displayTask(task: Task): void {
    if (this.task_form) {
      this.task_form.reset();
    }

    this.task = task;

    this.task_form.patchValue({
      id: this.task.id,
      name: this.task.name,
      parent_task: this.task.parent_task,
      child_task: this.task.child_task,
      create_by: this.task.create_by,
      create_date: this.task.create_date,
      status: this.task.status,
    });
  }

  getTask(id: string | null): void {
    this.task_service.getOneTask(id).subscribe({
      next: (task: Task) => this.displayTask(task)
    });
  }

  ngOnDestroy(): void {
      this.sub.unsubscribe();
  } 

  saveTask(): void {
    if(this.task_form.valid) {
      if(this.task_form.dirty) {
        const t: Task = {...this.task, ...this.task_form.value};
        if(t.id === '0') {
          this.task_service.createTask(t).subscribe({
            error: () => this.mess="Thêm mới thành công"
          });
        }
        else {
          this.task_service.updateTask(t).subscribe({
            error: () => this.mess="Chỉnh sửa thành công"
          });
        }
      }
      else {
        this.onSaveComplete();
      }
    }
    else {
      console.log("Task form bị lỗi: dòng 94");
    }
  }

  onSaveComplete(): void {
    this.task_form.reset();
    // this.router.navigate(['/task'])
  }


}
function output() {
  throw new Error('Function not implemented.');
}
