import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-expandgroup',
  templateUrl: './expandgroup.component.html',
  styleUrls: ['./expandgroup.component.css'],
})
export class ExpandgroupComponent {
  @Input() collapsee: boolean = false;
  @Output() collapseeChange = new EventEmitter<boolean>();

  changetocollapse() {
    this.collapseeChange.emit(true);
  }
}
<div class="p-2">
    <div class="relative ml-8 group">
        <button class="object-top p-1 mr-1 rounded hover:bg-gray-400 font-bold hidden group-hover:relative">...</button>
        <button class="hover:bg-blue-600" class="" (click)="changetocollapse()"><svg xmlns="http://www.w3.org/2000/svg" height="1em"
                viewBox="0 0 448 512"><!--! Font Awesome Free 6.4.0 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2023 Fonticons, Inc. -->
                <path
                    d="M201.4 342.6c12.5 12.5 32.8 12.5 45.3 0l160-160c12.5-12.5 12.5-32.8 0-45.3s-32.8-12.5-45.3 0L224 274.7 86.6 137.4c-12.5-12.5-32.8-12.5-45.3 0s-12.5 32.8 0 45.3l160 160z" />
            </svg></button>
        <span class="ml-2 font-medium text-blue-500 text-xl">This Month</span>
        <span class="pl-4 text-sm text-gray-500 hidden group-hover:relative">6 Task/ 2 Subitems </span>
    </div>
    
    
    <div class="border-y border-r border-l-4 border-l-indigo-500 p-2 ml-8 rounded">
        <span>Task1</span><br>
        <br>
        <span>Task2</span><br>
        <br>
        <span>Task3</span>
    </div>
</div>
