import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';
import { Component, OnInit } from '@angular/core';
import { AddGroupTaskService } from 'src/services/add-group-task-service/add-group-task.service';
import { GroupTaskListService } from 'src/services/group-task-list-service/group-task-list.service';
import { TaskListService } from 'src/services/task-list-service/task-list.service';

@Component({
  selector: 'app-boardcontent',
  templateUrl: './boardcontent.component.html',
  styleUrls: ['./boardcontent.component.css'],
  providers: [GroupTaskListService]
})
export class BoardcontentComponent implements OnInit {
  collapse: string[] = [];

  constructor(public group_task_list_service: GroupTaskListService, public task_list_service: TaskListService,
              public add_group_service: AddGroupTaskService) {};

  ngOnInit(): void {
      this.group_task_list_service.getGroupTaskList();
      this.task_list_service.getTaskList();
      this.add_group_service.buildForm('','');
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.group_task_list_service.group_task_list , event.previousIndex, event.currentIndex);
  }

  isDrawerOpen: boolean = false;
  tempTaskName: string = '';
  tempPerson: string = '';

  receiveInfo($event: { param1: string, param2: string }){
    this.isDrawerOpen = true;
    this.tempTaskName = $event.param1;
    this.tempPerson = $event.param2;
  }
}
<div class="pl-10 pt-4 relative" style= "height:500px; white-space: nowrap; overflow-y: scroll;">

    <div cdkDropList (cdkDropListDropped)="drop($event)">
        <div *ngFor="let group_task of group_task_list_service.group_task_list" cdkDrag class="mb-3">
            <div *ngIf="collapse.includes(group_task.id); else elsecollap">
                <app-expandgroup (openDrawer)="receiveInfo($event)" (openDrawer)="receiveInfo($event)" [group_task]="group_task" [(collapsee)]="collapse"></app-expandgroup>
            </div>
            
            <ng-template #elsecollap>
                <app-collapsegroup [group_task]="group_task" [(collapsec)]="collapse"></app-collapsegroup>
            </ng-template>
        </div>
    </div>

    <button class="mt-3">
        <div class="flex items-center p-2 border-2 border-gray-300 hover:bg-slate-300 rounded">
            <svg viewBox="0 0 20 20" fill="currentColor" width="20" height="20" aria-hidden="true"
                class="icon_4b23d45e02 leftIcon_0e9d75fef6 noFocusStyle_cff5f5a050" data-testid="icon">
                <path
                    d="M10.75 3C10.75 2.58579 10.4142 2.25 10 2.25C9.58579 2.25 9.25 2.58579 9.25 3V9.25H3C2.58579 9.25 2.25 9.58579 2.25 10C2.25 10.4142 2.58579 10.75 3 10.75H9.25V17C9.25 17.4142 9.58579 17.75 10 17.75C10.4142 17.75 10.75 17.4142 10.75 17V10.75H17C17.4142 10.75 17.75 10.4142 17.75 10C17.75 9.58579 17.4142 9.25 17 9.25H10.75V3Z"
                    fill="currentColor" fill-rule="evenodd" clip-rule="evenodd"></path>
            </svg>
            <span class="ml-2">
                <form [formGroup]="add_group_service.group_form">
                    <input
                        class="w-full py-0 placeholder:text-sm pl-5 border-y-white border-x-white group-hover:border-gray-300 rounded-md"
                        (keyup.enter)="add_group_service.saveGroup('')"  type="text" formControlName="name"
                        placeholder="+ Add Task">
                </form>
            </span>
        </div>
    </button>

</div>
<app-task-drawer [name]="tempTaskName" [person]="tempPerson" [isOpen]="isDrawerOpen" (closed)="isDrawerOpen = false"></app-task-drawer>
