import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { initPopovers } from 'flowbite';
import { Grouptask } from 'src/app/grouptask';
import { EditGroupTaskService } from 'src/services/edit-group-task-service/edit-group-task.service';
import { TaskListService } from 'src/services/task-list-service/task-list.service';

@Component({
  selector: 'app-collapsegroup',
  templateUrl: './collapsegroup.component.html',
  styleUrls: ['./collapsegroup.component.css']
})
export class CollapsegroupComponent implements OnInit {
  @Input() collapsec: string[]= [];
  @Input() group_task!: Grouptask;

  @Output() collapsecChange = new EventEmitter<string[]>();

  constructor (public taskListService: TaskListService, public task_list_service: TaskListService, 
              public edit_group_task_service: EditGroupTaskService) { }

  ngOnInit(): void {
    this.taskListService.getTaskList();
  }

  changetoexpand() {
    this.collapsec.push(this.group_task.id)
    this.collapsecChange.emit(this.collapsec);
  }
}
<div class="group">
    <div class="mt-2 pl-2 h-14 absolute left-px">
        <button class="p-1 hover:bg-slate-300 rounded-md">
            <div class=" group-hover:fill-currentColor">
                <svg viewBox="0 0 20 20" fill="currentColor" width="20" height="20" aria-hidden="true" class="icon_4b23d45e02 noFocusStyle_cff5f5a050" data-testid="icon"><path d="M6 10.5C6 11.3284 5.32843 12 4.5 12 3.67157 12 3 11.3284 3 10.5 3 9.67157 3.67157 9 4.5 9 5.32843 9 6 9.67157 6 10.5zM11.8333 10.5C11.8333 11.3284 11.1618 12 10.3333 12 9.50492 12 8.83334 11.3284 8.83334 10.5 8.83334 9.67157 9.50492 9 10.3333 9 11.1618 9 11.8333 9.67157 11.8333 10.5zM17.6667 10.5C17.6667 11.3284 16.9951 12 16.1667 12 15.3383 12 14.6667 11.3284 14.6667 10.5 14.6667 9.67157 15.3383 9 16.1667 9 16.9951 9 17.6667 9.67157 17.6667 10.5z" fill="white" class="group-hover:fill-black"></path></svg>
            </div>
        </button>
    </div>
    <div>
        <div class="flex flex-col p-1">
            <div class="flex flex-col border-solid py-1 {{group_task.color[0]}} border-gray-200 border-2 border-l-8 rounded-l-md">
                <div class="flex items-center">
                    <button (click)="changetoexpand()">
                        <svg viewBox="0 0 20 20" fill="currentColor" width="24" height="24" role="button" tabindex="0" aria-hidden="false" class="icon_4b23d45e02 clickable_f2c35f1e3f" data-testid="icon"><path d="M12.5303 9.46967L12 10L12.5303 10.5303C12.8232 10.2374 12.8232 9.76256 12.5303 9.46967ZM10.9393 10L7.46967 13.4697C7.17678 13.7626 7.17678 14.2374 7.46967 14.5303C7.76256 14.8232 8.23744 14.8232 8.53033 14.5303L12.5303 10.5303L12 10L12.5303 9.46967L8.53033 5.46967C8.23744 5.17678 7.76256 5.17678 7.46967 5.46967C7.17678 5.76256 7.17678 6.23744 7.46967 6.53033L10.9393 10Z" fill="currentColor" class="fill-blue-500" fill-rule="evenodd" clip-rule="evenodd"></path></svg>
                    </button>
                    <div (click)="edit_group_task_service.editCell(group_task.id)" (keyup.enter)="edit_group_task_service.saveCell(group_task)">
                        <span class="py-1 ml-2 text-xl text{{group_task.color}}500 font-medium">
                            <ng-container *ngIf="edit_group_task_service.edit_group_id === group_task.id; else displayCell">
                                <input (blur)="edit_group_task_service.saveCell(group_task)"
                                    class="w-full py-0 placeholder:text-sm px-0 border-y-white border-x-white group-hover:border-gray-300 rounded-md"
                                    type="text" [(ngModel)]="group_task.name">
                            </ng-container>
                            <ng-template #displayCell> <span style="
                                display:inline-block;
                                white-space: nowrap;
                                overflow: hidden;
                                text-overflow: ellipsis;
                                max-width: 25ch;">{{ group_task.name }}</span></ng-template>
                            <!-- {{group_task.name}} -->
                        </span>
                    </div>
                </div>
                <div>
                    <span class="ml-8 text-gray-500">{{task_list_service.countTask(group_task.id)}} Tasks</span>
                </div>
            </div>
        </div>
    </div>
</div>
