import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-collapsegroup',
  templateUrl: './collapsegroup.component.html',
  styleUrls: ['./collapsegroup.component.css']
})
export class CollapsegroupComponent {
  @Input() collapsec: boolean = false;
  @Output() collapsecChange = new EventEmitter<boolean>();

  changetoexpand() {
    this.collapsecChange.emit(false);
  }
}

<div>
    
</div>
<div class = "p-2 border-y border-r border-l-4 border-l-indigo-500 rounded">
    <button class="" (click)="changetoexpand()">
        <svg xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 320 512"><!--! Font Awesome Free 6.4.0 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2023 Fonticons, Inc. --><path d="M278.6 233.4c12.5 12.5 12.5 32.8 0 45.3l-160 160c-12.5 12.5-32.8 12.5-45.3 0s-12.5-32.8 0-45.3L210.7 256 73.4 118.6c-12.5-12.5-12.5-32.8 0-45.3s32.8-12.5 45.3 0l160 160z"/></svg>
    </button> 
    <span class="ml-2 font-medium text-blue-500 text-xl">This month</span>
    <div class="pl-4 text-sm text-gray-500">6 Task/ 2 Subitems</div>
</div>

<div class="relative group">
    <button class="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600">Hover me</button>
    <div class="absolute bg-gray-200 p-4 rounded-lg top-full left-0 transition-all duration-300 hidden group-hover:block">
      <!-- Nội dung của div -->
      <p>This is a hidden div.</p>
    </div>
</div>
