import { Component, OnInit } from '@angular/core';
import { Monday } from '../monday';
import { MondayService } from '../monday.service';

@Component({
  selector: 'app-monday-list',
  templateUrl: './monday-list.component.html',
  styleUrls: ['./monday-list.component.css']
})
export class MondayListComponent implements OnInit {
  pageTitle = 'Monday List';
  filteredMondays: Monday[] = [];
  mondays: Monday[] = [];
  errorMessage = '';

  _listFilter = '';
  get listFilter(): string {
    return this._listFilter;
  }
  set listFilter(value: string) {
    this._listFilter = value;
    this.filteredMondays = this.listFilter ? this.performFilter(this.listFilter) : this.mondays;
  }

  constructor(private mondayService: MondayService) { }

  performFilter(filterBy: string): Monday[] {
    filterBy = filterBy.toLocaleLowerCase();
    return this.mondays.filter((monday: Monday) =>
      monday.name.toLocaleLowerCase().indexOf(filterBy) !== -1);
  }

  ngOnInit(): void {
    this.getMondayData();
  }

  getMondayData() {
    this.mondayService.getMondays()
      .subscribe({
        next: (mondays) => {
          this.mondays = mondays;
          this.filteredMondays = mondays;
        },
        error: (err) => this.errorMessage = <any>err,
        complete: () => console.info('Get mondays in monday list')
      });
  }
}
