import { Component } from '@angular/core';
import { FlatTreeControl } from '@angular/cdk/tree';
import { MatTreeFlatDataSource, MatTreeFlattener } from '@angular/material/tree';

interface FoodNode {
  name: string;
  children?: FoodNode[];
}

interface ExampleFlatNode{
  expandable: boolean;
  name: string;
  level: number;
}

const TREE_DATE: FoodNode[] = [
  {
    name: 'Fruit',
    children: [{}]
  }

];

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  private _transfomer = (node: FoodNode, level: number) => {
    return {
      expandable: !!node.children && node.children.length > 0,
      name: node.name,
      level: level,
    };
  };

  threeControl = new FlatTreeControl<ExampleFlatNode>(
    node=>node.level,
    node=>node.expandable
  );

  threeFlattener = new MatTreeFlattener(
    this._transfomer,
    node=>node.level,
    node=>node.expandable,
    node=>node.children
  );

  dataSource= new MatTreeFlatDataSource(
    this.threeControl, this.threeFlattener 
  );

}
